import urequestsimport ujsonfrom machine import I2C, Pinfrom machine import RTCimport utimeimport ntptimep21 = Pin(21,Pin.IN,Pin.PULL_UP)p22 = Pin(22,Pin.IN,Pin.PULL_UP)i2c =I2C(scl=Pin(22), sda=Pin(21))rtc = RTC()ntptime.settime() tm = utime.localtime(utime.time()) # UTC nowjst = str(tm[0])+'/'+str(tm[1])+'/'+str(tm[2])+' '+str((tm[3]+9)%24)+':'+str(tm[4])+':'+str(tm[5])def readU5():    readtemp = i2c.readfrom_mem(0x48,0,1)    readtemp = int.from_bytes(readtemp,'little') & 0xFF    return readtempdef raw_temp():    readtemp = readU5()    T = ( readtemp << 8 | readU5() )>> 4    return Ttemp = raw_temp() * 0.0625print(temp)print(i2c)print(i2c.scan())print(jst)url = 'http://192.168.100.144/iot-study/recieve.php'data = {"data" : temp,        "name" : 'maki'        }header = {            'Content-Type' : 'application/json'        }res = urequests.post(        url,        data = ujson.dumps(data).encode("utf-8"),        headers = header    )print(res.status_code)res.close()